import React from "react";

function Body() {
  return (
    <div className="body">
      <div className="body-content">
        <p>
          {" "}
          Venture across the grim lands of Sorrowfen. Delve into dark,
          foe-infested dungeons. Outwit your opponents and reign champion of the
          land.{" "}
        </p>
        <br />
        <p>
          Little Dungeon Crawlers is a tabletop action RPG board game that takes
          the fantasy of dungeon crawlers and loot grinder games and compacts it
          both in size and in play time into a physical tabletop game.
        </p>
      </div>
      <div className="body-content">
        <p>
          Little Dungeon Crawlers is coming soon! Stay up to date with the
          latest announcements and development & design progress on our socials!
        </p>
      </div>
    </div>
  );
}

export default Body;
